import { useState, useEffect } from "react";
import { Route, Routes, BrowserRouter } from "react-router-dom";
import countriesJson from "./countries.json";
import TopPage from './pages/TopPage';
import WorldPage from "./pages/WorldPage";
import './App.css';

function App() {

  const [loading, setLoading] = useState(false);

  const [country, setCountry] = useState("");

  const [countryData, setCountryData] = useState({
    date: "",
    newConfirmed: "",
    totalConfirmed: "",
    newRecovered: "",
    totalRecovered: "",
  });

  const [allCountriesData, setAllCountriesData] = useState([]);

  useEffect(() => {
    const getCountryData = () => {
      setLoading(true);
      fetch(`https://monotein-books.vercel.app/api/corona-tracker/country/${country}`)
      .then(res => res.json())
      .then(data => {
        setCountryData({
          date: data[data.length -1].Date,
          newConfirmed: data[data.length -1].Confirmed - data[data.length -2].Confirmed,
          totalConfirmed: data[data.length -1].Confirmed,
          newRecovered: data[data.length -1].Recovered - data[data.length -2].Recovered,
          totalRecovered: data[data.length -1].Recovered
        });
        setLoading(false);
      })
        .catch(err => alert("エラーが発生しました。ページをリロードして、もう一度トライしてください。"));
    }
    getCountryData();
  });


  useEffect(() => {
      fetch("https://monotein-books.vercel.app/api/corona-tracker/summary")
      .then(res => res.json())
      .then(data => setAllCountriesData(data.Countries));
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<TopPage countriesJson={countriesJson} setCountry={setCountry} getCountryData={getCountryData} countryData={countryData} loading={loading} />} />
        <Route path="/world" element={<WorldPage allCountriesData={allCountriesData} />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
